import {activites, scenarios , quetes , taches} from "../../data/data";
import {accesScenarioIndice, listeScenario} from "./ScenarioHelper";
import {accesQueteIndice, listeQuete} from "./QueteHelper";
import {accesTacheIndice} from "./TacheHelper";
import {accesActiviteIndice} from "./ActiviteHelper";
import {compositions} from "../../types/TypeComposition";

export function mise_a_jour_na(){
    let activity=[];
    let scenario =[];
    let quest=[];
    let S;
    let Q=[ ];

    for (let i=0 ; i<activites.length;i++) {// on parcours la liste des activites

        S = listeScenario(activites[i].nom);//on sélectionne les scénario dont le père est A */
        for (let j = 0; j < S.length; j++) {// on parcours les scenarios d'une activite A

            /* Liste des quêtes d'un scénario donné */
            Q = listeQuete(S[j].nom);
            for (let k = 0; k < Q.length; k++) { //on parcours les quêtes du scenario i

                quest.push({id: '' + i + '_' + j + '_' + k, text: Q[k].nom});
            }

            scenario.push({id: i + '_' + j, expanded: true, text: S[j].nom, items: quest});
            quest = [];
        }

        activity.push({id: i + '', text: activites[i].nom, expanded: true, items: scenario});
        scenario=[];
    }
    return activity;
}

export function type_element(id){

    let i;

    if (activites[0].id === id){ //l'élement est une activite
        return compositions.ACTIVITE;
    }

    i=scenarios.findIndex(x=>x.id===id);

    if (i>-1){ //l'élement est un scenario
        return compositions.SCENARIO;

    }

    i=quetes.findIndex(x=>x.id===id);

    if (i>-1){ //l'élement est une quete
        return compositions.QUETE;

    }

    i=taches.findIndex(x=>x.id===id);

    if (i>-1){ //l'élement est une tache
        return compositions.TACHE;
    }

    return null;
}


export function rechercheNav(id){
    let res = type_element(id);
    let i;
    switch (res) {
        case compositions.QUETE:
            i=accesQueteIndice(id);
            return {nodes:quetes[i].nodes,links: quetes[i].links};

        case compositions.TACHE:
            i=accesTacheIndice(id);
            return {nodes:taches[i].nodes,links: taches[i].links};

        case compositions.SCENARIO:
            i=accesScenarioIndice(id);
            return {nodes:scenarios[i].nodes,links: scenarios[i].links};

        case compositions.ACTIVITE:
            i=accesActiviteIndice(id);
            return {nodes:activites[i].nodes,links: activites[i].links};

        default:
            return null;
    }
}
