import React, { Component } from 'react'
import {activites} from '../data/data'
import {update , onCloseModalScenario} from "../App";
import TextField from "@material-ui/core/TextField/index";
import Button from "@material-ui/core/Button/index";
import {compositions} from "../types/TypeComposition";

import * as Constants from '../configuration_app/Constants'
import {update_navigation} from "../navigation/Navigation";
import {getUniqueID} from "../fonctions/helper/AppHelper";

/*permet de créer une tâche*/
class CreateScenario extends Component {

    /* tâche */
    state = {
        x: 300,
        y: 300,
        id: '',
        color: Constants.GRAPH_NODES_DEFAULT_COLOR,
        type: compositions.TACHE,
        idPere:  ''
    };

    handleChange = id => event => {
        this.setState({ [id]: event.target.value });
    };

    handleSubmit = (e) => {
        console.log(getUniqueID());

        e.preventDefault();
        activites[0].nodes.push(this.state);
        console.log(activites[0]);
        onCloseModalScenario();
        update();
        //update_navigation();

    };

    render() {

        return (
            <div className="container">
                <div className="modal-content">
                    <form>
                        <TextField
                            id="standard-name"
                            label="Titre du scénario"
                            value={this.state.id}
                            onChange={this.handleChange('id')}
                            margin="normal"
                        />
                    </form>
                    <form>
                        <TextField
                            id="standard-name"
                            label="description"
                            multiline
                            rows="4"
                            value={this.state.description}
                            onChange={this.handleChange('Description.js')}
                            margin="normal"
                        />
                    </form>
                    <Button variant="outlined" color="primary" onClick={this.handleSubmit}>Créer</Button>
                </div>
            </div>
        )
    }
}

export default CreateScenario;
