import React, { Component } from "react";

import TreeView from "devextreme-react/tree-view";

import * as Constants from "../configuration_app/Constants";

import {activites} from "../data/data";

import {update} from "../App";
import {mise_a_jour_na, rechercheNav} from "../fonctions/helper/NavigationHelper";

//fonction qui met a jour la navigation
export function update_navigation (){
    let nav = mise_a_jour_na();
    this.setState({p:nav});
}

class Navigation extends Component {
    constructor(props) {
        super(props);

        this.state = {
            value: "contains",
            p:[],
        };

        this.itemClicked = this.itemClicked.bind(this);
        this.update_navigation = update_navigation.bind(this);
    }

    recherche=(id)=>{
        // renvoie la liste des nodes et des links
        return  rechercheNav(id);
    };


    itemClicked(e) {
        console.log('item :'+e.itemData.text)
        let r = this.recherche(e.itemData.text);
        activites[0].nodes=r.nodes;
        activites[0].links=r.links;
        console.log(activites[0].nodes);
        update();
    }

    componentWillMount() {
        this.update_navigation();
        console.log("test component monter");
    }



    render() {
        return (
            <React.Fragment>
                <TreeView
                    id={ "treeview" }
                    items={ this.state.p }
                    height={ Constants.NAV_DEFAULT_HEIGHT }
                    width={ Constants.NAV_DEFAULT_WIDTH }
                    searchMode= { Constants.NAV_DEFAULT_SEARCH_MODE }
                    searchEnabled={ Constants.NAV_DEFAULT_SEARCH_ENABLE }
                    selectionMode={'single'}
                    selectByClick={true}
                    onItemClick={ this.itemClicked }
                />
                {/*<button onClick={this.update_navigation}>update</button>*/}
            </React.Fragment>
        );
    }
}

export default Navigation;
