import React, {Component} from 'react'
import {selection} from "./data/data";
import ListEntity from './listes/ListEntity'
import {activites} from "./data/data";
import Button from "@material-ui/core/Button";
import TextField from '@material-ui/core/TextField';
import {update} from './App';
import PostConditionTache from './conditions/PostconditionTache';
import PostConditionComposition from './conditions/PostConditionComposition'
import {descriptionStyle} from './styles/styles'
import {compositions} from "./types/TypeComposition";

import * as Constants from './configuration_app/Constants';
import {rechercheNav} from "./fonctions/helper/NavigationHelper";


class NewDescription extends Component {

    /*state des modifications à apporter au noeud sélectionner (pour changer de nom ou de description) */
    state = {
        nom:"",
        description:"",

    };

    /* si un changement dans le nom ou la description, ce charge de l'enregistrer dans le state au dessus*/
    handleChange = id => event => {
        this.setState({ [id]: event.target.value });
    };

    /* gère la modification une fois valider, changement des sources et des targets des liens concernées */
    /* problèmme avec les liens  */
    handleSubmit = (e) => {
        e.preventDefault();

        for(let j=0;j<selection.nodes.length;j++){
            if (selection.nodes[j].id===selection.node.id) {
                selection.nodes[j].id=this.state.nom;
                break;
            }
        }
        console.log("sortie de la boucle ");
        for(let j=0;j<selection.links.length;j++){
            if (selection.links[j].target===selection.node.id) selection.links[j].target = selection.node.id;
            if (selection.links[j].source===selection.node.id) selection.links[j].source = selection.node.id;
        }

        let search =rechercheNav(selection.node.id);
        let tab = search.tab;
        let i = search.i;

        if (this.state.id !== "") tab[i].nom=this.state.nom;
        if (this.state.description !== "") tab[i].description=this.state.description;

        this.setState({nom:"", description:"", /*x: "", y: ""*/});
        update();

    };

    retourEntite = () => {
        let prec =activites[0].nodes.findIndex(v => v === activites[0].node);
        if (prec !== -1){
            activites[0].nodes[prec].color= Constants.GRAPH_NODES_DEFAULT_COLOR;
            activites[0].node= null; // aucun noeud n'est sélectionné
            this.setState({data : activites[0]})
        }
        update();
    };

    supprime_noeud = () => {

        activites[0].nodes = activites[0].nodes.filter(v => v!== activites[0].node);

        activites[0].links = activites[0].links.filter(v=> v.source !== activites[0].node.id);
        activites[0].links = activites[0].links.filter(v => v.target !== activites[0].node.id);
        activites[0].node=null;

        update();
    };
/*
    supprime_postcondition = () => {
        activites[0].links = activites[0].links.filter(v=> v.source !== activites[0].node.id);
        activites[0].links = activites[0].links.filter(v => v.target !== activites[0].node.id);

        update();
    };
*/
    render() {

        /* description du noeud sélectionné */
        if (activites[0].node !== null) {
            return (
                <div className="Description" style={descriptionStyle}>
                    <div>
                        {/* utilisation des forms de modal-ui, nom et description dans les forms */}
                        <form>
                            <TextField
                                id="standard-name"
                                label={"Nom: " + activites[0].node.id}
                                value={this.state.id}
                                onChange={this.handleChange('id')}
                                margin="normal"
                            />
                        </form>
                        <form>
                            <TextField
                                id="standard-name"
                                label={"Description: " + activites[0].node.description}
                                value={this.state.description}
                                onChange={this.handleChange('description')}
                                margin="normal"
                            />
                        </form>

                        <div className="flex1">
                            <Button variant="outlined" color="primary" onClick={this.handleSubmit}>modifier</Button>
                        </div>

                        <div className="ListLien">
                            <div>
                                {
                                    /* A-t-il au moins un successeur? Est-il une tâche? */
                                    /*noeudARelation(activites[0].node.id, activites[0].node.type, activites[0].node.idPere) && // Ne fonctionne pas pour le moment. Il faut un état dynamique. */
                                    activites[0].nodes[0].type === compositions.TACHE ? (<PostConditionTache/>) : (<PostConditionComposition/>)
                                }
                            </div>
                        </div>
                    </div>

                    <div className="RetourModif">
                        <div className="flex1">
                            <Button variant="outlined" color="primary" onClick={this.retourEntite}>retour Entité</Button>
                        </div>
                        <div className="flex1">
                            <Button variant="outlined" color="primary" onClick={this.supprime_noeud}>supprimer scénario</Button>
                        </div>
                    </div>
                </div>
            )
        } else {
            return (
                /* affichage des entité si pas de noeud sélectionné */
                <div className="Description" style={descriptionStyle}>
                    <div>
                        <ListEntity/>
                    </div>
                </div>
            )
        }
    }
}

export default NewDescription;
