import React, { Component } from 'react'
import {activites,selection} from '../data/data'
import {update , onCloseModalScenario} from "../App";
import TextField from "@material-ui/core/TextField/index";
import Button from "@material-ui/core/Button/index";
import {compositions,sousCategorie} from "../types/TypeComposition";
import {update_navigation} from "../navigation/Navigation";
import * as Constants from '../configuration_app/Constants'
import {getUniqueID} from "../fonctions/helper/AppHelper";
import {GRAPH_NODES_DEFAULT_COLOR} from "../configuration_app/Constants";

/*permet de créer une tâche*/
class CreateScenario extends Component {

    /* tâche */
    state = {

        id: '',
        description:'',
        nom:'',
        color: Constants.GRAPH_NODES_DEFAULT_COLOR,
        type: compositions.TACHE,
        idPere:  selection.id,
        postconditions: [],
    };

    handleChange = id => event => {
        this.setState({ [id]: event.target.value });
    };

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({id:getUniqueID()});
        activites[0].nodes.push(this.state);
        update();
        onCloseModalScenario();

        if (selection.id===0){
            activites.push(this.state);
        }
        else{
            let sous_cat = sousCategorie(selection.id).tab; //le type de noeud qu'on va creer
            selection.nodes.push({id:this.state.id ,nom:this.state.nom,color:GRAPH_NODES_DEFAULT_COLOR ,x:350,y:250,});
            sous_cat.push({id:this.state.id,description: this.state.description,
                idPere:selection.id,nodes:[],links:[],postconditions:[]});
            let pere= recherche(selection.id);
            pere.tab[pere.i].nodes=selection.nodes;
        }

        update();
        update_navigation();
        onCloseModalScenario();
    };

    render() {

        return (
            <div className="container">
                <div className="modal-content">
                    <form>
                        <TextField
                            id="standard-name"
                            label="Titre du scénario"
                            value={this.state.nom}
                            onChange={this.handleChange('nom')}
                            margin="normal"
                        />
                    </form>
                    <form>
                        <TextField
                            id="standard-name"
                            label="description"
                            multiline
                            rows="4"
                            value={this.state.description}
                            onChange={this.handleChange('description')}
                            margin="normal"
                        />
                    </form>
                    <Button variant="outlined" color="primary" onClick={this.handleSubmit}>Créer</Button>
                </div>
            </div>
        )
    }
}

export default CreateScenario;
